import React, { Component } from 'react'
import ClienteService from '../../../services/ClienteService'
import './style.css'
class Cadastro extends Component {
  state = {
    model: {
      id: 0,
      nome: '',
      origem: '',
      destino: '',
      saida: '',
      retorno: '',
      preco: 0,
    },
  }

  setValues = (e, field) => {
    const { model } = this.state
    model[field] = e.target.value
    this.setState({ model })
    console.log(this.state.model)
  }

  create = (e) => {
    this.setState({
      model: {
        id: 0,
        nome: '',
        origem: '',
        destino: '',
        saida: '',
        retorno: '',
        preco: 0,
      },
    })
    this.props.clienteCreate(this.state.model)
  }

  componentWillMount() {
    PubSub.subscribe('edit-cliente', (topic, cliente) => {
      this.setState({ model: cliente })
    })
  }

  render() {
    return (
      <div className="content--form--destino">
        <h2 className="text-center">Cadastro de clientes</h2>
        <div className="form">
          <form className="form-destino-action col-sm-12">
            <label>Nome: </label>
            <br />
            <input
              class="input"
              type="nome"
              value={this.state.model.nome}
              onChange={(e) => this.setValues(e, 'nome')}
            />
            <br />

            <label>Destino: </label>
            <br />
            <input
              class="input"
              type="destino"
              value={this.state.model.destino}
              onChange={(e) => this.setValues(e, 'destino')}
            />
            <br />

            <label>Origem: </label>
            <br />
            <input
              class="input"
              type="origem"
              value={this.state.model.origem}
              onChange={(e) => this.setValues(e, 'rigem')}
            />
            <br />

            <label>Saída: </label>
            <br />
            <input
              class="input"
              type="nome"
              value={this.state.model.saida}
              onChange={(e) => this.setValues(e, 'saida')}
            />
            <br />

            <label>Retorno: </label>
            <br />
            <input
              class="input"
              type="retorno"
              value={this.state.model.retorno}
              onChange={(e) => this.setValues(e, 'retorno')}
            />
            <br />

            <label>Preço: </label>
            <br />
            <input
              class="input"
              type="preco"
              value={this.state.model.preco}
              onChange={(e) => this.setValues(e, 'preco')}
            />
            <br />

            <button
              type="submit"
              className="btn btn-primary"
              onClick={this.create}
            >
              Gravar
            </button>
          </form>
        </div>
      </div>
    )
  }
}

class ListCliente extends Component {
  delete = (id) => {
    this.props.deleteCliente(id)
  }

  onEdit = (cliente) => {
    PubSub.publish('edit-cliente', cliente)
  }

  render() {
    const { clientes } = this.props

    return (
      <div>
        <h2 className="text-center mb-4">Lista de clientes</h2>
        <div className="table-wrapper">
          <table className="fl-table">
            <thead>
              <tr>
                <th>Cidade</th>
                <th>País</th>
                <th>Valor</th>
                <th>Ação</th>
              </tr>
            </thead>
