    fetch(this.Url)
      .then((response) => response.json())
      .then((clientes) => this.setState({ clientes }))
      .catch((e) => console.log(e))
  }

  create = (cliente) => {
    let data = {
      id: parseInt(cliente.id),
      cidade: cliente.cidade,
      pais: cliente.pais,
      valor: parseFloat(cliente.valor),
    }
    const requestInfo = {
      method: 'POST',
      body: JSON.stringify(data),
      headers: new Headers({
        'Content-type': 'application/json',
      }),
    }
    if (data.valor !== 0 && data.cidade !== '' && data.pais !== '') {
      fetch('http://localhost:8080/cliente/salvar', requestInfo)
        .then((response) => response.json())
        .then((newDestino) => console.log(newCcliente))
        .catch((e) => console.log(e))
    } else {
      alert('Favor informar todos os campos!')
      return
    }
  }

  delete = (id) => {
    fetch(`http://localhost:8080/destino/deletar/${id}`, { method: 'DELETE' })
      .then((rows) => {
        const destinos = this.state.destinos.filter(
          (destino) => destino.id !== id,
        )
        this.setState({ destinos })
      })
      .catch((e) => console.log(e))
  }

  render() {
    return (
      <div className="row destinos-action-container">
        <div className="">
          <FormDestino destinoCreate={this.create} />
        </div>
        <div className="">
          <ListDestino
            destinos={this.state.destinos}
            deleteDestino={this.delete}
          />
        </div>
      </div>
    )
  }
}

